mapper.select:
		
		select num, title, content, user_id, "date"
			from (
        			select /*+ INDEX_DESC(list_baord XPKCONTENT_LIST)*/
        			ROWNUM rn, num, title, content, user_id, "date"
        			from list_baord
        			where rownum <= #{pageNum} * #{amount}
				)
		where rn > (#{pageNum} - 1) * #{amount}
